import axios from "axios";
import { useEffect, useState } from "react";

function Playlist({ userId }) {
  const [songs, setSongs] = useState([]);
  const [song, setSong] = useState({ title: "", artist: "" });

  useEffect(() => {
    axios.get(`http://localhost:3000/songs/${userId}`)
      .then(res => setSongs(res.data));
  }, [userId]);

  const addSong = async () => {
    const res = await axios.post("http://localhost:3000/songs", {
      ...song,
      userId
    });
    setSongs([...songs, res.data]);
  };

  return (
    <>
      <h2>Mi lista de reproducción</h2>
      <input placeholder="Canción" onChange={e => setSong({...song, title:e.target.value})}/>
      <input placeholder="Artista" onChange={e => setSong({...song, artist:e.target.value})}/>
      <button onClick={addSong}>Agregar</button>

      <ul>
        {songs.map(s => <li key={s._id}>{s.title} - {s.artist}</li>)}
      </ul>
    </>
  );
}

export default Playlist;
